mapper.select:
SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_ADDR, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_ENABLED, MEMBER_GENDER, MEMBER_ROLE, MEMBER_REGDATE, MEMBER_PWEVENT
FROM MEMBER
WHERE MEMBER_ID=#{id}

SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_ADDR, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_ENABLED, MEMBER_GENDER, MEMBER_ROLE, MEMBER_REGDATE, MEMBER_PWEVENT
FROM MEMBER
WHERE MEMBER_EMAIL=#{email}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_NAME=#{name} AND MEMBER_EMAIL=#{email}

SELECT MEMBER_ID, MEMBER_PW, MEMBER_NAME, MEMBER_BIRTH, MEMBER_ADDR, MEMBER_PHONE, MEMBER_EMAIL, MEMBER_ENABLED, MEMBER_GENDER, MEMBER_ROLE, MEMBER_REGDATE, MEMBER_PWEVENT
FROM MEMBER
WHERE MEMBER_ID=#{id} AND MEMBER_NAME=#{name} AND MEMBER_EMAIL=#{email}
